import React, { Component } from "react";
import Joi from "joi-browser";
import Input from "./input";
import Select from "./select";
import RadioButtonInput from "./radioButtonInput";

class Form extends Component {
  state = {
    data: {},
    errors: {},
  };

  validate = () => {
    const options = { abortEarly: false };
    const { error } = Joi.validate(this.state.data, this.schema, options);
    if (!error) return null;

    const errors = {};
    for (let item of error.details) errors[item.path[0]] = item.message;
    return errors;
  };
  validateProperty = ({ name, value }) => {
    const obj = { [name]: value };
    const schema = { [name]: this.schema[name] };
    const { error } = Joi.validate(obj, schema);
    return error ? error.details[0].message : null;
  };
  handleSubmit = (e) => {
    e.preventDefault();

    const errors = this.validate();
    this.setState({ errors: errors || {} });
    if (errors) return;

    this.doSubmit();
  };
  handleChange = ({ currentTarget: input }) => {
    const errors = { ...this.state.errors };
    const errorMessage = this.validateProperty(input);
    if (errorMessage) errors[input.name] = errorMessage;
    else delete errors[input.name];

    const data = { ...this.state.data };
    data[input.name] = input.value;

    this.setState({ data, errors });
  };

  renderButton(label) {
    return (
      <button disabled={this.validate()} className="btn btn-primary">
        {label}
      </button>
    );
  }
  renderSelect(name, label, options) {
    const { data, errors } = this.state;
    return (
      <Select
        name={name}
        value={data[name]}
        label={label}
        options={options}
        onChange={this.handleChange}
        error={errors[name]}
      />
    );
  }
  renderInput(name, label, type = "text") {
    const { data, errors } = this.state;

    return (
      <Input
        type={type}
        name={name}
        value={data[name]}
        label={label}
        onChange={this.handleChange}
        error={errors[name]}
      />
    );
  }
  renderSurvery() {
    return (
      <table className="tb">
        <thead>
          <tr>
            <th>Select one of the options</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Da li ste ikada davali krv</td>
            <td>{this.renderInputRadioButton("question1", "Yes", "yes")}</td>
            <td>{this.renderInputRadioButton("question1", "No", "no")}</td>
          </tr>
          <tr>
            <td>Da li ste ikada bili odbijeni kao davalac krvi</td>
            <td>{this.renderInputRadioButton("question2", "Yes", "yes")}</td>
            <td>{this.renderInputRadioButton("question2", "No", "no")}</td>
          </tr>
        </tbody>
      </table>
    );
  }

  renderInputRadioButton(name, label, value, type = "radio") {
    return (
      <RadioButtonInput
        type={type}
        name={name}
        value={value}
        label={label}
        onChange={this.handleChange}
        checked={this.state.data[name] === value}
      />
    );
  }
}

export default Form;
