import React, { Component } from "react";
import { getCenter } from "../fakeCenters";
class CenterDetails extends Component {
  state = {
    data: {
      _id: "",
      name: "",
      location: "",
      address: "",
      rating: "",
    },
  };
  componentDidMount() {
    const centerId = this.props.match.params.id;

    const center = getCenter("5b21ca3eeb7f6fbccd471816");
    if (!center) return this.props.history.replace("/not-found");

    this.setState({ data: this.mapToViewModel(center) });
  }
  mapToViewModel(center) {
    return {
      _id: center._id,
      name: center.name,
      location: center.location,
      address: center.address,
      rating: center.rating,
    };
  }
  render() {
    return (
      <React.Fragment>
        <h1>Center details: {this.props.match.params.id} </h1>
        <h2>Center location: {this.state.data.location}</h2>
      </React.Fragment>
    );
  }
}

export default CenterDetails;
