import './App.css';
import NavBar from './components/navBar';
import LoginForm from "./components/loginForm";
import Logout from './components/logout';
import RegisterForm from "./components/registerForm"; 
import NotFound from "./components/notFound";
import React, { Component } from 'react';
import { Redirect, Route, Switch } from "react-router-dom";
import BloodDonationForm from './components/bloodDonationForm';
import Centers from './components/centers';
import CenterDetails from './components/centerDetails';
import RegisteredUserProfile from './components/registeredUserProfile';
import CentersTest from './components/centersTest';
import RegisteredUserFutureAppointments from './components/registeredUserFutureAppointments';
import RegisteredUserPastAppointments from './components/registeredUserPastAppointments';
import RegisteredUserHomepage from './components/registeredUserHomePage';
import ScheduleAppointment from './components/scheduleAppointment';
import jwtDecode from 'jwt-decode';

class App extends Component {
  state = {};

  componentDidMount() {
    try{
      const jwt = localStorage.getItem('token');
      const user = jwtDecode(jwt);
      this.setState({user});
    }
      catch(ex){}
  }

  render(){
  return (
    <React.Fragment>
      <NavBar user={this.state.user}/>
      <main className="container">
        <Switch>
          <Route path="/register" component={RegisterForm}></Route>
          <Route path="/login" component={LoginForm}></Route>
          <Route path="/logout" component={Logout}></Route>
          <Route path="/donationForm" component={BloodDonationForm}></Route>
          <Route path="/centers/:id" component={CenterDetails}></Route>
          <Route path="/centers" component={Centers}></Route>
          <Route path="/centersTest" component={CentersTest}></Route>
          <Route path="/pastAppointments" component={RegisteredUserPastAppointments}></Route>
          <Route path="/futureAppointments" component={RegisteredUserFutureAppointments}></Route>
          <Route path="/registeredUserProfile" component={RegisteredUserProfile}></Route>
          <Route path="/scheduleAppointment" component={ScheduleAppointment}></Route>
          <Route path="/homePage" component={RegisteredUserHomepage}></Route>
          <Route path="/not-found" component={NotFound}></Route>
          <Redirect from="/" exact to="/homePage" />
          <Redirect to="/not-found" />
        </Switch>
      </main>
    </React.Fragment>
  );
  }
}

export default App;
