import React, { Component } from "react";
import Joi from "joi-browser";
import Form from "./common/form";

class RegisterForm extends Form {
  state = {
    data: {
      email: "",
      password: "",
      repeatedPassword: "",
      name: "",
      lastName: "",
      gender: "",
      residenceAddress: "",
      city: "",
      country: "",
      phoneNumber: "",
      jmbg: "",
      profession: "",
      workLocation: "",
    },
    errors: {},
  };
  schema = {
    email: Joi.string().required().email().label("Email"),
    password: Joi.string().required().min(5).label("Password").messages({
      "string.base": `"" Mora biti string`,
      "string.empty": `"" Mora imati vrednost`,
    }),
    repeatedPassword: Joi.ref("password"),
    name: Joi.string().required().label("Name"),
    lastName: Joi.string().required().label("Last Name"),
    gender: Joi.string().required().label("Gender"),
    residenceAddress: Joi.string().required().label("Residence Address"),
    city: Joi.string().required().label("City"),
    country: Joi.string().required().label("Country"),
    phoneNumber: Joi.string().required().label("Phone Number"),
    jmbg: Joi.string().length(13).regex(/^\d+$/).required().label("jmbg"),
    profession: Joi.string().required().label("Profession"),
    workLocation: Joi.string().required().label("Work Location"),
  };
  doSubmit = () => {
    console.log("Form submitted.");
  };

  render() {
    return (
      <div>
        <h1>Register</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("email", "Email")}
          {this.renderInput("password", "Password", "password")}
          {this.renderInput("repeatedPassword", "Repeat Password", "password")}
          {this.renderInput("name", "Name")}
          {this.renderInput("lastName", "Last Name")}
          {this.renderInput("gender", "Gender")}
          {this.renderInput("jmbg", "JMBG")}
          {this.renderInput("phoneNumber", "Phone Number")}
          {this.renderInput("country", "Country")}
          {this.renderInput("city", "City")}
          {this.renderInput("residenceAddress", "Residence Address")}
          {this.renderInput("profession", "Profession")}
          {this.renderInput("workLocation", "Work Location")}

          {this.renderButton("Register")}
        </form>
      </div>
    );
  }
}

export default RegisterForm;
