import React, { Component } from "react";
import http from "../services/httpService";
import Form from "./common/form";
import Joi from "joi-browser";
/*
const RegisteredUserProfile = ({ user }) => {
  return (
    <React.Fragment>
      <h1>My profile:</h1>
      <div>
        Profile type: {user.role === "RegUser" ? "Registered user" : "Employee"}
      </div>
    </React.Fragment>
  );
};

export default RegisteredUserProfile;
*/
class RegisteredUserProfile extends Form {
  state = {
    user: {},
    data: {
      email: "",
      /*
      name: "",
      lastName: "",
      gender: "",
      residenceAddress: "",
      city: "",
      country: "",
      phoneNumber: "",
      jmbg: "",
      profession: "",
      workLocation: "",*/
    },
    errors: {},
  };
  schema = {
    email: Joi.string()
      .required()
      .email()
      .label("Email")
      .error(() => {
        return {
          message: "Please enter a valid email address.",
        };
      }),
  };
  async componentDidMount() {
    const userId = this.props.user.id;
    console.log(userId);
    const { data: userInfo } = await http.get(
      "https://localhost:7293/api/User/GetSingleUser?Id=2bc7512e-4904-4d3c-b94f-9fb8824c315e"
    );

    this.setState({
      user: this.props.user,
      data: this.mapToViewModel(userInfo),
    });
  }
  mapToViewModel(userInfo) {
    return {
      email: userInfo.email,
      /*
      name: userInfo.name,
      lastName: userInfo.lastName,
      residenceAddress: userInfo.residenceAddress,
      city: userInfo.city,
      country: userInfo.country,
      phoneNumber: userInfo.phoneNumber,
      jmbg: userInfo.jmbg,
      profession: userInfo.profession,
      workLocation: userInfo.workLocation,*/
    };
  }
  doSubmit = () => {
    console.log("Profile info saved.");
  };
  render() {
    return (
      <React.Fragment>
        <h1>My profile:</h1>
        <div>
          Profile type:{" "}
          {this.state.user.role === "RegUser" ? "Regular user" : "Employee"}
          <form onSubmit={this.handleSubmit}>
            {this.renderInput("email", "Email")}
            {this.renderButton("Save changes")}
          </form>
        </div>
      </React.Fragment>
    );
  }
}

export default RegisteredUserProfile;
