import React, { Component } from "react";
handleChangeRadio = (e) => {
  this.setState({ [e.target.name]: e.target.value });
};
const DonationSurvey = ({ question1, question2, onChange }) => {
  return (
    <table>
      <thead>
        <tr>
          <th>Odaberite jednu od opcija</th>
          <th>Da</th>
          <th>Ne</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>Da li ste ikada davali krv</td>
          <td>
            <input
              type="radio"
              value="yes"
              name="question1"
              checked={question1 === "yes"}
              onChange={this.handleChangeRadio}
            />
          </td>
          <td>
            <input
              type="radio"
              value="no"
              name="question1"
              checked={question1 === "no"}
              onChange={this.handleChangeRadio}
            />
          </td>
        </tr>
        <tr>
          <td>Da li ste ikada bili odbijeni kao davalac krvi</td>
          <td>
            <input
              type="radio"
              value="yes"
              name="question2"
              checked={question2 === "yes"}
              onChange={this.handleChangeRadio}
            />
          </td>
          <td>
            <input
              type="radio"
              value="no"
              name="question2"
              checked={question2 === "no"}
              onChange={this.handleChangeRadio}
            />
          </td>
        </tr>
      </tbody>
    </table>
  );
};

export default DonationSurvey;
