import React, { Component } from "react";
import http from "../services/httpService";
import Form from "./common/form";
import Joi from "joi-browser";
import auth from "../services/authService";

class RegisteredUserProfile extends Form {
  state = {
    user: {},
    data: {
      id: "",
      userId: "",
      email: "",
      phoneNumber: "",
      /*
      name: "",
      lastName: "",
      gender: "",
      residenceAddress: "",
      city: "",
      country: "",
     
      jmbg: "",
      profession: "",
      workLocation: "",*/
    },
    errors: {},
  };
  schema = {
    email: Joi.string()
      .required()
      .email()
      .label("Email")
      .error(() => {
        return {
          message: "Please enter a valid email address.",
        };
      }),
    phoneNumber: Joi.string().label("Phone Number"),
  };
  async componentDidMount() {
    const currentUser = auth.getCurrentUser();

    const { data: userInfo } = await http.get(
      "https://localhost:7293/api/RegUser/GetSingleRegUserByEmail?Email=" +
        currentUser.email
    );
    console.log(userInfo);

    this.setState({
      user: this.props.user,
      data: this.mapToViewModel(userInfo),
    });
  }
  mapToViewModel(userInfo) {
    return {
      email: userInfo.user.email,
      phoneNumber: userInfo.phoneNumber,
      id: userInfo.id,
      userId: userInfo.user.id,
      /*
      name: userInfo.name,
      lastName: userInfo.lastName,
      residenceAddress: userInfo.residenceAddress,
      city: userInfo.city,
      country: userInfo.country,
      
      jmbg: userInfo.jmbg,
      profession: userInfo.profession,
      workLocation: userInfo.workLocation,*/
    };
  }
  doSubmit = async () => {
    await http.put("https://localhost:7293/api/RegUser/UpdateRegUser", {
      id: this.state.id,
      userId: this.state.userId,
      phone: this.state.phoneNumber,
    });
    console.log("Profile info saved.");
  };
  render() {
    return (
      <React.Fragment>
        <h1>My profile:</h1>
        <div>
          Profile type:{" "}
          {this.state.user.role === "RegUser" ? "Regular user" : "Employee"}
          Email {this.state.data.email}
          <form onSubmit={this.handleSubmit}>
            {this.renderInput("phoneNumber", "Phone number")}
            {this.renderButton("Save changes")}
          </form>
        </div>
      </React.Fragment>
    );
  }
}

export default RegisteredUserProfile;
