import React, { Component } from "react";
import { getCenter } from "../fakeCenters";
import axios from "axios";
class CenterDetails extends Component {
  state = {
    data: {
      _id: "",
      name: "",
      location: "",
      address: "",
      rating: "",
      appointments: [],
    },
  };
  async componentDidMount() {
    const centerId = this.props.match.params.id;

    const { data: center } = await axios.get(
      "https://localhost:44303/api/centers/" + centerId
    );

    console.log(center);
    //const center = getCenter(centerId);
    if (!center) return this.props.history.replace("/not-found");

    this.setState({ data: this.mapToViewModel(center) });
  }
  mapToViewModel(center) {
    return {
      _id: center._id,
      name: center.name,
      location: center.location,
      address: center.address,
      rating: center.rating,
    };
  }
  render() {
    return (
      <React.Fragment>
        <h1>
          Center name: {this.state.data.name} <br></br>
          Center location: {this.state.data.location} <br></br>
          Center address: {this.state.data.address} <br></br>
          Center rating: {this.state.data.rating} <br></br>
          Free appointments:
          <ul>
            {center.map((item) => (
              <li key={item.id}>{item.name}</li>
            ))}
          </ul>
        </h1>
      </React.Fragment>
    );
  }
}

export default CenterDetails;
