import React, { Component } from "react";
import Joi from "joi-browser";
import _ from "lodash";
import Form from "./common/form";
import { Survey } from "survey-react";
import SurveyComponent from "./surveyComponent";
import "survey-react/survey.css";

class BloodDonationForm extends Form {
  state = {
    data: {
      name: "",
      lastName: "",
      birthDate: "",
      jmbg: "",
      gender: "",
      phoneNumber: "",
      residenceAddress: "",
      numberOfPreviousDonations: "",
      location: "",
      question1: "noAnswer",
      question2: "noAnswer",
      question3: "noAnswer",
    },
    errors: {},
  };
  schema = {
    name: Joi.string().required().label("Name"),
    lastName: Joi.string().required().label("Last Name"),
    birthDate: Joi.string().required().label("Birth Date"),
    gender: Joi.string().required().label("Gender"),
    jmbg: Joi.string().required().label("JMBG"),
    phoneNumber: Joi.string().required().label("Phone Number"),
    residenceAddress: Joi.string().required().label("Residence Address"),
    numberOfPreviousDonations: Joi.string()
      .required()
      .label("Number of Previous Donations"),
    location: Joi.string().required().label("Location"),
    question1: Joi.string().required(),
    question2: Joi.string().required(),
    question3: Joi.string().required(),
  };
  doSubmit = () => {
    console.log("Form submitted.");
  };
  onChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  };
  render() {
    const { question1, question2, question3 } = this.state;
    return (
      <div>
        <h1>Blood donation form</h1>
        <form onSubmit={this.handleSubmit}>
          {this.renderInput("name", "Name")}
          {this.renderInput("lastName", "Last Name")}
          {this.renderInput("birthDate", "Birth Date")}
          {this.renderInput("jmbg", "JMBG")}
          {this.renderInput("gender", "Gender")}
          {this.renderInput("location", "Location")}
          {this.renderInput("residenceAddress", "Residence Address")}
          {this.renderInput("phoneNumber", "Phone Number")}
          {this.renderInput(
            "numberOfPreviousDonations",
            "Number of Previous Donations"
          )}
          <table>
            <thead>
              <tr>
                <th>Odaberite jednu od opcija</th>
                <th>Da</th>
                <th>Ne</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Da li ste ikada davali krv</td>
                <td>
                  <input
                    type="radio"
                    value="yes"
                    name="question1"
                    checked={question1 === "yes"}
                    onChange={this.onChange}
                  />
                </td>
                <td>
                  <input
                    type="radio"
                    value="no"
                    name="question1"
                    checked={question1 === "no"}
                    onChange={this.onChange}
                  />
                </td>
              </tr>
              <tr>
                <td>Da li ste ikada bili odbijeni kao davalac krvi</td>
                <td>
                  <input
                    type="radio"
                    value="yes"
                    name="question2"
                    checked={question2 === "yes"}
                    onChange={this.onChange}
                  />
                </td>
                <td>
                  <input
                    type="radio"
                    value="no"
                    name="question2"
                    checked={question2 === "no"}
                    onChange={this.onChange}
                  />
                </td>
              </tr>
            </tbody>
          </table>

          {this.renderButton("Submit")}
        </form>
      </div>
    );
  }
}

export default BloodDonationForm;
