import React, { Component } from "react";
/*
const RegisteredUserProfile = ({ user }) => {
  return (
    <React.Fragment>
      <h1>My profile:</h1>
      <div>
        Profile type: {user.role === "RegUser" ? "Registered user" : "Employee"}
      </div>
    </React.Fragment>
  );
};

export default RegisteredUserProfile;
*/
class RegisteredUserProfile extends Component {
  state = {
    user: {},
    data: {
      email: "",
      name: "",
      lastName: "",
      gender: "",
      residenceAddress: "",
      city: "",
      country: "",
      phoneNumber: "",
      jmbg: "",
      profession: "",
      workLocation: "",
    },
  };
  async componentDidMount() {
    const { data: userInfo } = await http.get(
      "https://localhost:7293/api/User/GetSingleUser"
    );

    this.setState({
      user: this.props.user,
      data: this.mapToViewModel(userInfo),
    });
  }
  mapToViewModel(userInfo) {
    return {
      id: userInfo.id,
      name: userInfo.name,
      location: userInfo.location,
      address: userInfo.address,
      rating: userInfo.rating,
      appointments: userInfo.appointments,
    };
  }
  render() {
    return (
      <React.Fragment>
        <h1>My profile:</h1>
        <div>
          Profile type:{" "}
          {this.state.user.role === "RegUser" ? "Regular user" : "Employee"}
        </div>
      </React.Fragment>
    );
  }
}

export default RegisteredUserProfile;
